import { getDicts } from '@/api/system/dict'

export default {
  name: 'DictSelect',
  props: {
    value: {
      type: [String, Number],
      default: ''
    },
    size: {
      type: String,
      default: 'mini'
    },
    dictName: {
      type: String,
      default: '',
      require: true
    },
    placeholder: {
      type: String,
      default: ''
    }
  },
  data() {
    return {
      dictList: [],
      dictValue: ''
    }
  },
  watch: {
    value() {
      this.dictValue = this.value + ''
    }
  },
  created() {
    this.dictValue = this.value + ''
    this.getDictList()
  },
  methods: {
    getDictList() {
      // 从后台获取字典列表
      getDicts(this.dictName).then(response => {
        this.dictList = response.data
      })
    },
    change(value) {
      this.$emit('input', value)
    }
  }
}
